import React from 'react'

function Movie(props) {
  const {countries} = props;
  console.log(countries.map((index) => (index)));

  return (
    countries.map((country, index) => (
      <div className="col-3" key={index}>
        <div className="col">
          <div className="card my-1">
            <img src={country.flags[0]} className="card-img-top" alt="name" />
            <div className="card-body">
              <h6 className="card-title">{country.name.common}</h6>
              <p className="card-subtitle mb-2 text-muted"><span className="font-weight-bold">Population</span>: {country.population.toLocaleString()}</p>
              <p className="card-text"><span className="font-weight-bold">Region</span>: {country.region}</p>
              <p className="card-text"><span  className="font-weight-bold">Capital</span>:{country.capital}</p>
          </div>
          </div>
        </div>
      </div>
    ))

  ) 
}





export default Movie
